import { userConstants } from '../constants';
import { makePOSTRequest } from 'shared-lib/src/Axios';

export const organizationActions = { newOrganization };

export function newOrganization(reqparams) {
  return dispatch => {
    try{
      dispatch({ type: userConstants.PAGE_LOADING });
      makePOSTRequest('/api/v1/organizations', reqparams)
      .then(response => {
        if(response.data.status === "ok"){
          dispatch({type: userConstants.INITIAL_DATA, payload: response})
        }else {
          dispatch({type: userConstants.INITIAL_DATA_ERROR, payload: response.error})
        }
      })
    }catch(e){
      dispatch( {
        type: userConstants.AUTHENTICATION_FAILURE,
        payload: console.log(e),
      })
    }
  }
}
