import React, { useState, useEffect } from 'react';
import { connect, useDispatch } from 'react-redux';
import { initialActions, userActions} from '../../../actions';
import { Button, Table, Modal } from 'react-bootstrap';


function OrganizationList(props) {
	const dispatch = useDispatch();

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(() => {
    dispatch(initialActions.initialData());
  }, []);

  function logout(response) {
    dispatch(userActions.logout());
  }

  if (props.loading) {
    return <div className="page_loading">Loading..</div>
  }

  return (
   <div  className="container">
    <div className="page_header">
      <h3> Organizations </h3>
      <Button variant="primary" onClick={handleShow}> Launch demo modal</Button>
    </div>
    <Table striped bordered hover>
      <thead>
        <tr>
          <th width="3%" className="text-center">S.No</th>
          <th>Display name</th>
          <th>Business name</th>
          <th>Platform notes</th>
          <th width="5%">Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>1</td>
          <td>Mark</td>
          <td>Otto</td>
          <td>@mdo</td>
          <td>
            <div className="d-flex align-items-center justify-content-between">
              <div><i class="fa fa-pencil-square-o" aria-hidden="true"></i></div>
              <div><i class="fa fa-trash-o" aria-hidden="true"></i></div>
            </div>
          </td>
        </tr>
      </tbody>
    </Table>
    <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
  </div>
  );
}

export default OrganizationList
