import React, { useState, useEffect } from 'react';
import { connect, useDispatch, useSelector } from 'react-redux';
import { organizationActions} from '../../../actions';
import { Button, Table, Modal, CloseButton } from 'react-bootstrap';
import OrganizationForm from '../organization/form'


function OrganizationList(props) {

  const organizationList = useSelector(state => state.organization.organizationList);
	const dispatch = useDispatch();
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(() => {
    dispatch(organizationActions.getOrganization())
  }, []);

  const showResult = (values) => {
    dispatch(organizationActions.newOrganization(values, handleClose));
  }

  const handleEdit = (id) => {
    dispatch(organizationActions.editOrganizaion(id))
  }

  if (props.loading) {
    return <div className="page_loading">Loading..</div>
  }

  return (
   <div  className="container">
    <div className="page_header d-flex align-items-center justify-content-between mb-3">
      <h3> Organizations </h3>
      <Button variant="primary" onClick={handleShow}>New Organization</Button>
    </div>
    <Table striped bordered hover>
      <thead>
        <tr>
          <th width="3%" className="text-center">S.No</th>
          <th>Display name</th>
          <th>Business name</th>
          <th>Platform notes</th>
          <th width="5%">Actions</th>
        </tr>
      </thead>
      <tbody>
      {organizationList && organizationList.length > 0 ? 
        organizationList.map((data, index) => {
          <tr key={index}>
            <td>{index + 1}</td>
            <td>{data.display_name}</td>
            <td>{data.business_name}</td>
            <td>{data.platform_notes}</td>
            <td>
              <div className="d-flex align-items-center justify-content-between">
                <div onClick={() => handleEdit(data.id)}><i className="fa fa-pencil-square-o" aria-hidden="true"></i></div>
                <div><i className="fa fa-trash-o" aria-hidden="true"></i></div>
              </div>
            </td>
          </tr>

        })

        :null

      }
      </tbody>
    </Table>

    <Modal show={show} onHide={handleClose} backdrop="static" keyboard={false} className="custom-modal" centered>
      <Modal.Header>
        <Modal.Title id="example-custom-modal-styling-title"> Add Organization </Modal.Title>
        <CloseButton variant="white" onClick={handleClose}/>
      </Modal.Header>
      <Modal.Body>
        <OrganizationForm 
          onSubmit={showResult} 
          handleClose={handleClose}
        />
      </Modal.Body>
    </Modal>
  </div>
  );
}

export default OrganizationList
